import { ref } from 'vue'

const getItemsByCategory = (a) => {
    const items = ref(null)
    const error = ref(null)

    const load = async () => {
        try{
            let data = await fetch('http://localhost:3000/items?category=' + a)
            if(!data.ok){
                throw Error('No data exist.')
            }
            items.value = await data.json()
            console.log("getItemsCategory loaded")
        }
        catch(err) {
            error.value = err.message
            console.log(error.value);
        }
    }

    load()

    return { items, error, load }
}

export default getItemsByCategory
